import java.util.Scanner;/** * @param * @author Administrator * @date 2021/4/12 0012 20:20 * @throws Exception */public class l1_50 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int m =sc.nextInt();        String s = " ";        int i =0;        for(i = 0;i<n;i++){            s+="a";        }        double t = Math.pow(26,n);        int q  =(int)t-m;        char[] c =new char[6];        for(i=0;i<n;i++){            c[i]=(char)(97+q%26);            q=q/26;        }        for(int j =i-1;j>=0;j--){            System.out.print(c[j]);        }    }}