import java.io.BufferedReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;import java.util.Scanner;/** * @param * @author Administrator * @date 2021/4/12 0012 20:41 * @throws Exception */public class l1_54 {    public static void main(String[] args) throws IOException {        Scanner sc = new Scanner(System.in);        String str= sc.next();        int n =sc.nextInt();        sc.nextLine();        int[][] img = new int[n][n];        for(int i=0;i<n;i++){            String str1= sc.nextLine();            for(int j=0;j<n;j++){                if(str1.charAt(j)=='@'){                    img[i][j]=1;                }            }        }        boolean flag=true;        for(int i=0;i<n;i++){            for(int j=0;j<n;j++){                if(img[i][j]!=img[n-i-1][n-j-1]){                    flag=false;                }            }        }        if(flag){            System.out.println("bu yong dao le");        }        for(int i=n-1;i>=0;i--){            for(int j=n-1;j>=0;j--){                if(img[i][j]==1){                    System.out.print(str);                }else{                    System.out.print(" ");                }            }            System.out.println();        }    }}