import javax.security.sasl.SaslClient;import java.util.Scanner;/** * @param * @author Administrator * @date 2021/4/12 0012 20:58 * @throws Exception */public class l1_55 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int count1=0;        int count2=0;        int Pa = sc.nextInt();        int Pb = sc.nextInt();        int[] arr = new int[3];        for(int i =0;i<arr.length;i++){            arr[i]=sc.nextInt();            if(arr[i]==0){                count1++;            }            if(arr[i]==1){                count2++;            }        }        if(count1==3){            System.out.println("The winner is a: "+Pa+" + "+count1);        }        else if(count2==3){            System.out.println("The winner is b: "+Pb+" + "+count2);        }else if(Pa>Pb&&count1>=1){            System.out.println("The winner is a: "+Pa+" + "+count1);        }else if(Pa < Pb &&count1>=1){            System.out.println("The winner is b: "+Pb+" + "+count2);        }    }}