import java.util.Scanner;/** * @param * @author Administrator * @date 2021/4/12 0012 19:57 * @throws Exception */public class test_049 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] arr = new int[n];        int max = 0;        for (int i = 0; i < n; i++) {            arr[i] = sc.nextInt();            if (arr[i] > max) {                max = arr[i];            }        }        int[] school = new int[n];        int[][] res = new int[n][105];        int cnt = 1;        if (n == 1) {            cnt = 0;        }        for (int i = 0; i < 10 * max; i++) {            for (int j = 0; j < n; j++) {                if (i < arr[j] * 10) {                    int num = 0;                    for (int k = 0; k < n; k++) {                        if (school[k] == 0) {                            num++;                        }                    }                    if (num == 1) {                        cnt += 1;                        res[j][i] = cnt++;                    } else {                        res[j][i] = cnt++;                    }                } else {                    school[j] = 1;                }            }        }        for (int i = 0; i < n; i++) {            System.out.println("#" + (i + 1));            for (int j = 0; j < 10 * arr[i]; j++) {                if ((j+1) % 10 == 0 && j != 0) {                    System.out.println(res[i][j]);                } else {                    System.out.print(res[i][j]+" ");                }            }        }    }}