import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.ArrayList;import java.util.Arrays;import java.util.Collections;import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/31 0031 19:58 * @throws Exception */public class exam11 {    public static void main(String[] args) throws IOException {        BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));        try {            String str = bf.readLine();            int N = Integer.parseInt(str.split(" ")[0]);            int k = Integer.parseInt(str.split(" ")[1]);            int M = Integer.parseInt(str.split(" ")[2]);            double sum = 0;            double avg = 0;            int[][] a = new int[N][k];            for (int i = 0; i < N; i++) {                String strings = bf.readLine();                String[] strings1 = strings.split(" ");                for (int j = 0; j < k; j++) {                    a[i][j] = Integer.parseInt(strings1[j]);                }            }            ArrayList<Double> list = new ArrayList<>();            for (int i = 0; i < N; i++) {                Arrays.sort(a[i]);                for (int j = 1; j < k - 1; j++) {                    sum += a[i][j];                }                avg = sum / (k - 2);                list.add(avg);                avg = 0;                sum = 0;            }            int i;            Collections.sort(list);            for (i = N - M; i < list.size(); i++) {                System.out.printf("%.3f", list.get(i));                if (i != list.size() - 1) {                    System.out.print(" ");                }            }        } catch (IOException e) {            e.printStackTrace();        }    }}