import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/31 0031 19:00 * @throws Exception */public class exam4 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        char[] c = sc.next().toCharArray();        int g = 0, l = 0, p = 0, t = 0;        for (char c1 : c) {            if (c1 == 'G' || c1 == 'g') {                g++;            }            if (c1 == 'P' || c1 == 'p') {                p++;            }            if (c1 == 'L' || c1 == 'l') {                l++;            }            if (c1 == 'T' || c1 == 't') {                t++;            }        }        while (true) {            if (g == 0 && p == 0 && l == 0 && t == 0) {                break;            }            if (g != 0) {                System.out.print("G");                g--;            }            if (p != 0) {                System.out.print("P");                p--;            }            if (l != 0) {                System.out.print("L");                l--;            }            if (t != 0) {                System.out.print("T");                t--;            }        }    }}