import java.util.*;import java.util.stream.Collectors;/** * @param * @author Administrator * @date 2021/3/31 0031 19:31 * @throws Exception */public class exam8 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        char[] c1 =sc.next().toCharArray();        Set<Integer> set = new HashSet<>();        for (int i = 0; i < c1.length; i++) {            set.add((int) c1[i] - 48);        }        List<Integer> list = new ArrayList<>(set);        Collections.reverse(list);        int[] index=new int[11];        for(int i=0;i<11;i++){            for(int j=0;j<list.size();j++){                if((int)c1[i]-48==list.get(j)){                    index[i]=j;                }            }        }        System.out.print("int[] arr = new int[]{");        for(int i=0;i<list.size();i++){            System.out.print(list.get(i));            if(i!=list.size()-1){                System.out.print(",");            }        }        System.out.print("};"+"\n"+"int[] index = new int[]{");        for(int i=0;i<index.length;i++){            System.out.print(index[i]);            if(i!=index.length-1){                System.out.print(",");            }        }        System.out.print("};");    }}