import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/31 0031 17:08 * @throws Exception */public class l1017 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String str =sc.next();        boolean fu = false;        boolean ou = false;        double count=0;        double result=0;        int length =str.length();        if((str.charAt(length-1)-'0')%2==0){            ou=true;        }        if((str.charAt(0)+"").equals("-")){            length=str.length()-1;            fu=true;        }        for(int i=0;i<str.length();i++){            if((str.charAt(i)+"").equals("2")){                count++;            }        }        if(ou&&fu){            result=count/length*1.5*2;        }        if(ou&&fu==false){            result=count/length*2;        }        if(fu&&ou==false){            result=count/length*1.5;        }        if(fu==false&&ou==false){            result=count/length;        }        System.out.printf("%.2f",result*100);        System.out.println("%");    }}