import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/26 0026 19:00 * @throws Exception */public class test001 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n =sc.nextInt();        char c =sc.next().charAt(0);        int m= (int) Math.sqrt((n+1)/2.0);        for(int i=0;i<2*m-1;i++){            for(int j=0;j<2*m-1;j++){                if((i>j&&i+j<2*m-2)||(i<j&&i+j>2*m-2)){                    if(i>j&&i+j<2*m-2)                        System.out.print(" ");                 else{                        System.out.print(c);                    }                }                System.out.println();            }System.out.println(n-2*m*m+1);        }    }}