import java.io.BufferedReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;import java.util.HashMap;import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/26 0026 9:12 * @throws Exception */public class test01{    public static void main(String[] args) throws IOException {        Scanner sc = new Scanner(System.in);        long num = sc.nextInt();        long len=0,first=0,s=1;        for(int i=2;i<Math.sqrt(num);i++){            s=1;            for(int j=i;s*i<=num;j++){                s*=j;                if(num%s==0&&j-i+1>len){                    first=i;                    len=j-i+1;                }            }        }        if(first==0){            first=num;            len=1;        }        System.out.println(len);        for(int i=0;i<len-1;i++){            System.out.print(first+i+"*");        }        System.out.println(first+len-1);    }}