import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;/** * @param * @author Administrator * @date 2021/3/27 0027 14:53 * @throws Exception */public class test010 {    public static void main(String[] args) throws IOException {        BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));        String[] s = bf.readLine().split(" ");        int[] its = new int[s.length];        for(int i =0;i<s.length;i++){            its[i]=Integer.parseInt(s[i]);        }        quickSort(its,0,its.length-1);        System.out.println(join(its,"->"));    }    private static void quickSort(int[] arr, int low, int high) {        if(low>=high){            return ;        }        int i =low ,j=high,temp=arr[low];        while(i<j){            while(arr[j]>=temp&&i<j){                j--;            }            while(arr[i]<=temp&&i<j){                i++;            }            if(i<j){                int t=arr[i];                arr[i]=arr[j];                arr[j]=t;            }        }        arr[low]=arr[i];        arr[i]=temp;        quickSort(arr,low,i-1);        quickSort(arr,j+1,high);    }    public static String join(int[] arr,String reg){        String str="";        for(int i=0;i<arr.length;i++){            str+=arr[i];            if(i==arr.length-1) {                continue;            }            str+=reg;        }        return str;    }}