import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;/** * @param * @author Administrator * @date 2021/3/27 0027 16:57 * @throws Exception */public class test013 {    private static int num =1;    public static void main(String[] args) {        BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));        try{            int n =Integer.parseInt(bf.readLine());            int sum=0;            for(int i=1;i<=n;i++){                sum+=factorial(i);                num=1;            }            System.out.println(sum);        } catch (IOException e) {            e.printStackTrace();        }    }    private static int factorial(int i) {        if(i==1) {            return 1;        }        num*=i*factorial(i-1);        return num;    }}