import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/27 0027 17:05 * @throws Exception */public class test015 {    public static void main(String[] args) {        BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));        try {            String[] strings = bf.readLine().split(" ");            double n = Double.parseDouble(strings[0]);            String str = strings[1];            for (int i = 0; i < Math.round(n/2); i++) {                for (int j = 0; j < n; j++) {                    System.out.print(str);                }                System.out.println();            }        } catch (IOException e) {            e.printStackTrace();        }    }}