import java.io.BufferedReader;import java.io.InputStream;import java.io.InputStreamReader;import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/26 0026 14:19 * @throws Exception */public class test02 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int num = sc.nextInt();        if(num<0){            System.out.print("fu ");        }        String str = String.valueOf(Math.abs(num));        String[] list ={"ling","yi","er","san","si","wu","liu","qi","ba","jiu"};        for(int k =0;k<str.length()-1;k++){            int s = str.charAt(k)-'0';            System.out.print(list[s]+" ");        }        int s= str.charAt(str.length()-1)-'0';        System.out.println(list[s]);    }}