import java.util.Scanner;/** * @param * @author Administrator * @date 2021/3/26 0026 14:36 * @throws Exception */public class test3 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String[] str = sc.nextLine().split(" ");        int a =Integer.parseInt(str[0]);        int b =Integer.parseInt(str[1]);        if(a>=-100&&a<=100){            int len=b-a;            int count=0;            int sum=0;            for(int i=0;i<=len;i++){                sum+=a;                System.out.printf("%5d",a);                a+=1;                count++;                if(count%5==0){                    System.out.println();                    count=0;                }            }            if(count!=0){                System.out.println();            }            System.out.println("Sum = "+sum);        }    }}